package test0822;

public class Test0913 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("김건주");
		
		
		// 숫자형 변수 a의 값은 1 입니다.
		// a의 값이 1이면 참이고 2이면 거짓입니다. 라는 코드를 작성
		
		int a = 1;
		if (a == 1) {
			System.out.print("참입니다.");
			
		} else if (a == 2) {
			System.out.println("거짓입니다.");
			
		}
		
		
		
		
		//for문
		
		for (int i = 1; i<10;i++) {
			System.out.println("1");
			
		}
		
		// 1을 열번을 출력하기
		
//		초기식;
//		while(증가식) {
//			
//			반복식;
//			
//		}
		
		
		int i=1;
		while(i<=10) {
			System.out.println("2");
			i++;
			
		}
	}

}

